@section('title', 'Calculator')
@push('scripts')
    <script src="{{ asset('assets/js/calculator.js') }}"></script>
    <script>
        var elt = document.getElementById('calculator');
        var calculator = Desmos.GraphingCalculator(elt);
        // calculator.setExpression({latex: 'x^2+y^2=10'});

        var btnElt = document.getElementById('screenshot-button');
        btnElt.addEventListener('click', captureScreenshots);

        var containerElt = document.getElementById('screenshot-container');
        var img1x = document.getElementById('screenshot-1x');
        var img2x = document.getElementById('screenshot-2x');

        function captureScreenshots() {
            img1x.src = calculator.screenshot({
                height: 500,
                width: 500,
                targetPixelRatio: 2
            });

            containerElt.style.display = 'block';
        }
    </script>
@endpush
